import React from "react";

export const Todo = ({todo,checkDone,id,deleteJob,handleEditTodo,editTodo,isEmptyObj,setEditTodo}) =>{

	  // Onchange EditTodo
		const handleOnchangeEditTodo = (e) => {
			let editTodoCopy = {...editTodo}
			editTodoCopy.name = e.target.value
			setEditTodo(editTodoCopy)
			console.log(editTodoCopy)
		} 

  return (
    <li className={todo.isCompleted === true ? "done" : null}>

			{/* 
				- check điều kiện khi nào thì cho input edit hiện ra
				- load lần đầu {} -> true - click set todo vào -> false
				- nếu editTodo === todoname in ra input ngược lại in name ban đầu
			*/}

      {isEmptyObj === true ?
				<p>{id + 1} - {todo.name}</p>
				:
				<>
					{ editTodo.name === todo.name ?
						<p> 
							{id + 1} - 
							<input
								value={editTodo.name}
								onChange={(e) => handleOnchangeEditTodo(e)}

							/>
						</p>
						:
						<p>{id + 1} - {todo.name}</p>

					}
				</>
      }

      

      <p className="icon">
        <button className="edit" onClick={() => handleEditTodo(todo)}>Edit</button>
        <span className="icon-done" onClick={() => checkDone(id)}></span>
        <span className="close" onClick={() => deleteJob(id)}></span>
      </p>

    </li>
  );
};
