import React from "react";

export const Todo = ({ todo, checkDone, id, deleteJob, handleEditTodo}) => {

  return (
    <li className={todo.isCompleted === true ? "done" : null}>
      <p>{todo.name}</p>
      {/* <input value={todo.name} onChange={() =>{}}/> */}

      <span> {/* check điều kiện khi nào thì cho input edit hiện ra */}

      </span>

      <p className="icon">
        <button onClick={() => handleEditTodo(todo)}>Edit</button>
        <span className="icon-done" onClick={() => checkDone(id)}></span>
        <span className="close" onClick={() => deleteJob(id)}></span>
      </p>

    </li>
  );
};
